const showHidePwAct = document.querySelectorAll('.showHidePwAct'),
   showHidePwNew = document.querySelectorAll('.showHidePwNew'),
   showHidePwConf = document.querySelectorAll('.showHidePwConf'),
   actualPass = document.querySelectorAll('.actualPass'),
   newPass = document.querySelectorAll('.newPass'),
   confNewPass = document.querySelectorAll('.confNewPass');
   
showHidePwAct.forEach(eyeIcon => {
   eyeIcon.addEventListener('click', () => {
      actualPass.forEach(pwField => {
         if (pwField.type === 'password') {
            pwField.type = 'text';
            showHidePwAct.forEach(icon => {
               icon.classList.replace('bx-hide', 'bx-show');
            });
         } else {
            pwField.type = 'password';
            showHidePwAct.forEach(icon => {
               icon.classList.replace('bx-show', 'bx-hide');
            });
         }
      });
   });
});

showHidePwNew.forEach(eyeIcon => {
   eyeIcon.addEventListener('click', () => {
      newPass.forEach(pwField => {
         if (pwField.type === 'password') {
            pwField.type = 'text';
            showHidePwNew.forEach(icon => {
               icon.classList.replace('bx-hide', 'bx-show');
            });
         } else {
            pwField.type = 'password';
            showHidePwNew.forEach(icon => {
               icon.classList.replace('bx-show', 'bx-hide');
            });
         }
      });
   });
});

showHidePwConf.forEach(eyeIcon => {
   eyeIcon.addEventListener('click', () => {
      confNewPass.forEach(pwField => {
         if (pwField.type === 'password') {
            pwField.type = 'text';
            showHidePwConf.forEach(icon => {
               icon.classList.replace('bx-hide', 'bx-show');
            });
         } else {
            pwField.type = 'password';
            showHidePwConf.forEach(icon => {
               icon.classList.replace('bx-show', 'bx-hide');
            });
         }
      });
   });
});