function validar(cad) {
   var total = 0;
   var longitud = cad.length;
   var longcheck = longitud - 1;
   if (cad !== '' && longitud === 10) {
      for (i = 0; i < longcheck; i++) {
         if (i % 2 === 0) {
            var aux = cad.charAt(i) * 2;
            if (aux > 9) aux -= 9;
            total += aux;
         } else {
            total += parseInt(cad.charAt(i)); // parseInt o concatenará en lugar de sumar
         }
      }

      total = total % 10 ? 10 - total % 10 : 0;

      if (cad.charAt(longitud - 1) == total) {
         Swal.fire({
            position: 'top-end',
            icon: 'success',
            title: 'Cédula Válida',
            text: 'Std ha proporcinado una cédula válida.',
            showConfirmButton: false,
            allowOutsideClick: false,
            timer: 1500
         });
         
         return document.getElementById('cedula').value = cad;
      } else {
         Swal.fire({
            position: 'top-end',
            icon: 'error',
            title: 'Cédula no válida',
            text: 'Por favor proporcionar una cedula válida.',
            showConfirmButton: false,
            allowOutsideClick: false,
            timer: 1500
         });
         
         return document.getElementById('cedula').value = cad;
      }
   }
}

function Cedula(Control) {
   var cad = document.getElementById('cedula').value.trim();
   var longitud = cad.length;
   if (longitud === 10) {
      Control.value = validar(Control.value);
   }
}

